body.script:
    $(function() {
        // ж IE     
        if (navigator.appName == "Microsoft Internet Explorer" && parseInt(navigator.appVersion.split(";")[1].replace(/[ ]/g, "").replace("MSIE", "")) < 9) {
            $("body").append("");
            //console.log("您的浏览器版本过低，请使用IE9及以上版本");
            $(".login-page").css("height", $(window).height());
            $(".login-container").css("filter", "progid:DXImageTransform.Microsoft.gradient(startColorstr=#70000000,endColorstr=#70000000)");
            $(".blue-qcode").css("display", "none");
            $(".default-checkbox").css({
                "margin-top": "11px",
                "margin-left": "35px"
            });
        }
        var loginMargintop = $(window).height() - $('.login-container').height();
        var loginMarginright = $(window).width() - $('.login-container').width();
        var logoMarginright = $(window).width() - $('.logo').width();

        $('.login-container').css({
            'right': loginMarginright / 2,
            'margin-top': loginMargintop / 2 + 10
        });
        $(".ding-QR").css({
            'right': loginMarginright / 2,
            'margin-top': loginMargintop / 2 + 10
        });

        if ($(window).width() > 1440) {

            $('.logo').css({
                'top': '20%',
                'right': logoMarginright / 2
            });

        } else {

            $('.logo').css({
                'top': '5%',
                'right': logoMarginright / 2
            });
        }
    });
    $(window).resize(function() {

        var loginMargintop = $(window).height() - $('.login-container').height();
        var loginMarginright = $(window).width() - $('.login-container').width();
        var logoMarginright = $(window).width() - $('.logo').width();

        $('.login-container').css({
            'right': loginMarginright / 2,
            'margin-top': loginMargintop / 2 + 10
        });

        if ($(window).width() > 1440) {

            $('.logo').css({
                'top': '20%',
                'right': logoMarginright / 2
            });

        } else {

            $('.logo').css({
                'top': '5%',
                'right': logoMarginright / 2
            });
        }

    });

    !function (window, document) {
        function d(a) {
			var url = "https%3a%2f%2foapi.dingtalk.com%2fconnect%2foauth2%2fsns_authorize%3fappid%3ddingoayiy2etqcpmyc0lpk%26response_type%3dcode%26scope%3dsnsapi_login%26state%3dSTATE%26redirect_uri%3dhttps%253A%252F%252Fzjuam.zju.edu.cn%252Fcas%252Flogin%253Fclient_name%253DDingDingClient";
			var service = GetQueryString("service");
			if (service != null) {
				url = url + encodeURIComponent(encodeURIComponent("&service="+encodeURIComponent(service)));
			}

            var e, c = document.createElement("iframe"),
                d = "https://login.dingtalk.com/login/qrcode.htm?goto=" + url ;
            d += a.style ? "&style=" + encodeURIComponent(a.style) : "",
                d += a.href ? "&href=" + a.href : "",
                c.src = d,
                c.frameBorder = "0",
                c.allowTransparency = "true",
                c.scrolling = "no",
                c.width =  a.width ? a.width + 'px' : "365px",
                c.height = a.height ? a.height + 'px' : "400px",
                e = document.getElementById(a.id),
                e.innerHTML = "",
                e.appendChild(c)
        }
        window.DDLogin = d
    }(window, document);


    function showDingQr() {
        $('#username').after($('#username').prop("outerHTML")).remove();
        $('#password').after($('#password').prop("outerHTML")).remove();
        $(".ding-QR").show();
        $(".login-container").hide();

