			style: "border:none;background-color:#FFFFFF;",
			width : "300",
			height: "350"
		});
    }

    function returnPre() {
        $('#username').after($('#username').prop("outerHTML")).remove();
        $('#password').after($('#password').prop("outerHTML")).remove();
        $(".ding-QR").hide();
        $(".login-container").show();

    }
	function GetQueryString(name) {
		var reg = new RegExp("(^|&)"+ name +"=([^&]*)(&|$)");
		var r = window.location.search.substr(1).match(reg);
		if(r!=null){
			return  unescape(r[2]);
		}else{
			return null;
		}
	}
    var handleMessage = function (event) {
        var origin = event.origin;
        console.log("origin", event.origin);
        if( origin == "https://login.dingtalk.com" ) { //判断是否来自ddLogin扫码事件。
            var loginTmpCode = event.data;
            //获取到loginTmpCode后就可以在这里构造跳转链接进行跳转了

            window.location.href="https://oapi.dingtalk.com/connect/oauth2/sns_authorize?appid=dingoayiy2etqcpmyc0lpk&response_type=code&scope=snsapi_login&state=STATE&redirect_uri=REDIRECT_URI&loginTmpCode="+loginTmpCode;
        }
    };
    if (typeof window.addEventListener != 'undefined') {
        window.addEventListener('message', handleMessage, false);
    } else if (typeof window.attachEvent != 'undefined') {
        window.attachEvent('onmessage', handleMessage);
    }

